import React from 'react';
import Questions from '../data/questions.json';
import Answers from '../data/answers.json';

//Functional Component Question

type Props = {
	question: string;
	answers: string[];
	callback: any;
	userAnswer: any;
	questionIndex: number;
	totalQuestions: number;
};

const Question: React.FC<Props> = ({question, answers, callback, userAnswer, questionIndex, totalQuestions}) => (
	<div className='question'>
		<div className='quizQuestion'>
			<h1>{Questions[questionIndex].question}</h1>
			<p>Question: {questionIndex + 1}</p>
		</div>
		<div className="quizAnswer">
			<p>
				{Questions[questionIndex].options.map((option: string, index: number) =>
					<button disabled={userAnswer} onClick={callback} key={index} className="optionButton">
						{option}
					</button>
				)}
			</p>
		</div>
	</div>
);

export default Question;
